mapper.select:
SELECT p_date
FROM product
WHERE p_num = #{p_num}

SELECT p_num
FROM product
WHERE p_num = #{p_num}

mapper.update:
UPDATE product
SET   a_price = #{a_price}
    , p_plus = #{p_plus}
    , a_count = #{a_count}
    , classify = #{classify}
    , customer_id = #{customer_id}
    , a_nprice = #{a_nprice}
    , p_status = #{p_status}
    , flag_1 = #{flag_1}
    , flag_2 = #{flag_2}
    , flag_3 = #{flag_3}
    , flag_4 = #{flag_4}
    , flag_5 = #{flag_5}
    , bid_checked = #{bid_checked}
WHERE p_num = #{p_num}

mapper.select:

WHERE true
ORDER BY p_num DESC

LIMIT #{offset}, #{pageSize}
